import Link from 'next/link';
import React, { Component, useRef } from 'react';
import { RawQuery,TableColumn } from './component/dbComponent';


export default class List extends Component {

    constructor() {
        super();
        this.state = {
            listHead: [],
            listItem: []
        };
        this.reloadComponent = this.reloadComponent.bind(this);
    }

    componentDidMount = async () => {
        await this.getHead();
        await this.reloadComponent();
    }

    reloadComponent = async () => {
        const sql = `SELECT * FROM employee;`;
        const value = await RawQuery(sql);
        return this.setState({listItem:value});
    }

    getHead = async () => {
        const colName = await TableColumn(`employee`);
        console.log(colName);
    }

    render() {
        return (
            <React.Fragment>
                <nav>
                    <li><Link href="/index"><a>home</a></Link></li>
                    <li><Link href="/list"><a>list</a></Link></li>
                    <li><Link href="/requestlist"><a>request list</a></Link></li>
                </nav>
            </React.Fragment>
        );
    }
}

const TableList = props => {
    return(
        <table>
            <thead>

            </thead>

            <tbody>

            </tbody>
        </table>
    );
}
